mapper.select:
SELECT * FROM USER_TABLE 
WHERE USER_ID=#{user_loginid}
AND USER_PWD=#{user_pwd}

SELECT COUNT(*) FROM (SELECT G.GESI_ID ,G.GESI_TITLE ,G.GESI_CONTENT ,G.GESI_DATE ,G.GESI_STATUS ,G.GESI_ANSDATE ,G.GESI_ANSCONTENT ,G.USER_ID ,U.USER_NAME ,G.GESI_CODE ,T.GESI_NAME FROM GESI G JOIN GESITYPE T ON T.GESI_CODE = G.GESI_CODE JOIN USER_TABLE U ON U.USER_ID = G.USER_ID
AND G.GESI_DATE >= #{startD} AND G.GESI_DATE <= #{endD}
AND G.GESI_CODE = #{gesi_code}
AND INSTR(G.GESI_TITLE, #{keyword}) > 0
AND INSTR(G.GESI_CONTENT, #{keyword}) > 0
AND INSTR(U.USER_NAME, #{keyword}) > 0
)

SELECT RN , GESI_ID , GESI_TITLE , GESI_CONTENT , GESI_DATE , GESI_STATUS , GESI_ANSDATE , GESI_ANSCONTENT , USER_ID , USER_NAME , GESI_CODE , GESI_NAME FROM ( SELECT ROWNUM RN , A.GESI_ID , A.GESI_TITLE , A.GESI_CONTENT , A.GESI_DATE , A.GESI_STATUS , A.GESI_ANSDATE , A.GESI_ANSCONTENT , A.USER_ID , A.USER_NAME , A.GESI_CODE , A.GESI_NAME FROM (SELECT G.GESI_ID ,G.GESI_TITLE ,G.GESI_CONTENT ,G.GESI_DATE ,G.GESI_STATUS ,G.GESI_ANSDATE ,G.GESI_ANSCONTENT ,G.USER_ID ,U.USER_NAME ,G.GESI_CODE ,T.GESI_NAME FROM GESI G JOIN GESITYPE T ON T.GESI_CODE = G.GESI_CODE JOIN USER_TABLE U ON U.USER_ID = G.USER_ID
AND G.GESI_DATE >= #{startD} AND G.GESI_DATE <= #{endD}
AND G.GESI_CODE = #{gesi_code}
AND INSTR(G.GESI_TITLE, #{keyword}) > 0
AND INSTR(G.GESI_CONTENT, #{keyword}) > 0
AND INSTR(U.USER_NAME, #{keyword}) > 0
ORDER BY G.GESI_DATE DESC) A) WHERE RN BETWEEN #{start} AND #{end}
